    async def quotecmd(self, message):
        """–ü–æ–ª—É—á–∏—Ç—å —Å–ª—É—á–∞–π–Ω—É—é —Ü–∏—Ç–∞—Ç—É: .quote"""
        lang = self.config["language"]
        quotes = self.quotes_ru if lang == "russian" else self.quotes_en
        if not quotes:
            await utils.answer(message, self.strings["no_quote"])
            return
        quote = random.choice(quotes)
        await utils.answer(message, self.strings["quote_prefix"] + quote)